package com.example.firebaseapplication.ui.profileimport android.content.Contextimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.WindowManagerimport com.example.firebaseapplication.databinding.ActivityUserProfileBindingimport com.example.firebaseapplication.ui.notification.NotificationActivityimport com.example.firebaseapplication.ui.users.UsersActivityimport com.example.firebaseapplication.ui.userauthentication.UserAuthActivityimport com.google.firebase.auth.FirebaseAuthimport com.google.firebase.database.FirebaseDatabaseclass UserProfileActivity : AppCompatActivity() {    companion object {        fun newIntent(context: Context): Intent {            return Intent(context, UserProfileActivity::class.java)        }    }    private val auth: FirebaseAuth by lazy { FirebaseAuth.getInstance() }    private lateinit var binding: ActivityUserProfileBinding    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding = ActivityUserProfileBinding.inflate(layoutInflater)        setContentView(binding.root)        window.setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_STATE_HIDDEN)        initView()    }    private fun initView() {        with(binding) {            tvLogOut.setOnClickListener {                auth.signOut()                startActivity(UserAuthActivity.newIntent(this@UserProfileActivity))                this@UserProfileActivity.finish()            }            tvUsers.setOnClickListener {                startActivity(UsersActivity.newIntent(this@UserProfileActivity))            }            btnAddUser.setOnClickListener {                val name = tvName.text.toString()                val address = tvAddress.text.toString()                val contact = tvContact.text.toString()                val user = User(name, address, contact)                firebaseDatabaseInstance(user)            }            tvNotification.setOnClickListener { startActivity(NotificationActivity.newIntent(this@UserProfileActivity)) }        }    }    private fun firebaseDatabaseInstance(user: User) {        val database = FirebaseDatabase.getInstance().reference        database.child(user.contact).setValue(user)    }}